INSERT INTO cms_pages VALUES (1,1,2,NULL,NULL,'Home',NULL,'/','<div id=\"myCarousel\" class=\"carousel slide\">\r\n  <div class=\"carousel-inner\">\r\n    <div class=\"item\">\r\n      <img src=\"http://farm4.staticflickr.com/3670/10624066103_77128e745b_k.jpg\" alt=\"\">\r\n      <div class=\"container\">\r\n        <div class=\"carousel-caption\">\r\n          <img src=\"/assets/Logo_100.png\" style=\"min-width: 0%; position: relative\" class=\"pull-left\"/>\r\n          <h1 style=\"padding-left: 110px\">A multi-scale society</h1>\r\n          <p class=\"lead\" style=\"padding-left: 110px\">No matter what gauge, scale or ability - DEMU welcomes all modellers interested in diesel and electric traction.</p>\r\n          <a class=\"btn btn-large btn-primary\" href=\"#\">Sign up today</a>\r\n        </div>\r\n      </div>\r\n    </div>\r\n    <div class=\"item\">\r\n      <img src=\"http://farm8.staticflickr.com/7435/10623791965_23f92c9f47_k.jpg\" alt=\"\">\r\n      <div class=\"container\">\r\n        <div class=\"carousel-caption\">\r\n          <img src=\"/assets/Logo_100.png\" style=\"min-width: 0%; position: relative\" class=\"pull-left\"/>\r\n          <h1 style=\"padding-left: 110px\">A multi-scale society</h1>\r\n          <p class=\"lead\" style=\"padding-left: 110px\">No matter what gauge, scale or ability - DEMU welcomes all modellers interested in diesel and electric traction.</p>\r\n          <a class=\"btn btn-large btn-primary\" href=\"#\">Sign up today</a>\r\n        </div>\r\n      </div>\r\n    </div>\r\n    <div class=\"item active\">\r\n      <img src=\"http://farm4.staticflickr.com/3780/10623808024_b34d72af6a_k.jpg\" alt=\"\">\r\n      <div class=\"container\">\r\n        <div class=\"carousel-caption\">\r\n          <img src=\"/assets/Logo_200.png\" style=\"min-width: 0%; position: relative\" class=\"pull-left\"/>\r\n          <h1>Welcome to DEMU</h1>\r\n          <p class=\"lead\" style=\"padding-bottom: 40px; padding-left: 210px\">The society for the modeller of the British Diesel and Electric era.</p>\r\n          <a class=\"btn btn-large btn-primary\" href=\"#\">Sign up today</a>\r\n        </div>\r\n      </div>\r\n    </div>\r\n  </div>\r\n  <a class=\"left carousel-control\" href=\"#myCarousel\" data-slide=\"prev\">‹</a>\r\n  <a class=\"right carousel-control\" href=\"#myCarousel\" data-slide=\"next\">›</a>\r\n</div>\r\n\r\n<div class=\"container marketing\">\r\n  \r\n  <div class=\"alert fade in\">\r\n      <button type=\"button\" class=\"close\" data-dismiss=\"alert\">×</button>\r\n      <strong>Update 71:</strong> Members should have received U71 (August 2013). Please contact the Treasurer if you havent - details on the Forum.\r\n  </div>\r\n  \r\n  <!-- Three columns of text below the carousel -->\r\n  <div class=\"row\">\r\n    <div class=\"span4\">\r\n      <img class=\"img-circle\" data-src=\"holder.js/140x140\" alt=\"140x140\" style=\"width: 140px; height: 140px; \" src=\"http://farm8.staticflickr.com/7347/10457927943_b1a14da19c_q.jpg\">\r\n      <h2>Modelling</h2>\r\n      <p>Promoting the best modelling of the post-modernisation plan era through our quarterly members magazine <strong>Update</strong> and roadshow visiting many exhibitions all over the country. Members enjoy reduced entrance fees to our annual <strong>Showcase</strong> exhibition.</p>\r\n      <p><a class=\"btn\" href=\"#\">View details »</a></p>\r\n    </div><!-- /.span4 -->\r\n    <div class=\"span4\">\r\n      <img class=\"img-circle\" data-src=\"holder.js/140x140\" alt=\"140x140\" style=\"width: 140px; height: 140px; \" src=\"http://farm4.staticflickr.com/3745/10328369764_8c3c1c4b15_q.jpg\">\r\n      <h2>Prototype Research</h2>\r\n      <p>Our <strong>Wiki, Forum</strong> and <strong>Drawing Exchange Service</strong> help to share prototype information amongst members. We often have trips by our Area Groups to visit and measure prototypes.</p>\r\n      <p><a class=\"btn\" href=\"#\">View details »</a></p>\r\n    </div><!-- /.span4 -->\r\n    <div class=\"span4\">\r\n      <img class=\"img-circle\" data-src=\"holder.js/140x140\" alt=\"140x140\" style=\"width: 140px; height: 140px; \" src=\"http://farm9.staticflickr.com/8096/8405138809_a0bb0dfb0c_q.jpg\">\r\n      <h2>Community</h2>\r\n      <p>Our <strong>Forum</strong> lets members connect with one another and share modelling tips and advice. Our <strong>Area Groups</strong> span the length and breadth of the UK. Come along and meet us other D&amp;E modellers at one of our informal area group meet-ups.</p>\r\n      <p><a class=\"btn\" href=\"#\">View details »</a></p>\r\n    </div><!-- /.span4 -->\r\n  </div><!-- /.row -->\r\n  \r\n</div>\r\n\r\n',0,2,1,0,'2013-11-01 18:07:33','2013-11-02 14:38:46'),(3,1,1,1,4,'Showcase','showcase','/showcase',NULL,0,2,1,0,'2013-11-01 21:09:34','2013-11-02 08:30:24'),(4,1,1,3,NULL,'Showcase 2014','showcase-2014','/showcase/showcase-2014','<h1> Showcase 2014 </h1>\r\n<div class=\"row\">\r\n\r\n  <div class=\"span9\">\r\n    <p>DEMU Showcase is the premier UK exhibition dedicated to diesel and electric modelling and will be on the weekend of 31 May &amp; 1 June 2014.</p>\r\n    \r\n    <h3>Opening Times</h3>\r\n    <p>Saturday 31 May 2014 10.00 - 17.00<br>\r\n    Sunday 1 June 2014 10.00 - 16.00</p>\r\n    \r\n    <h3>Venue</h3>\r\n    <p>Burton Town Hall, King Edward Place, Burton upon Trent, DE14 2EB. <a href=\"http://goo.gl/maps/tzIFh\">Map</a></p>  \r\n    \r\n    <p>The Town Hall is situated very close to the railway station in Burton upon Trent.  There is a public car park at the Town Hall, please be aware that East Staffs Borough Council have recently made this a pay and display car park.</p>\r\n    \r\n  </div>\r\n  \r\n  <div class=\"span3\" style=\"padding-bottom: 20px\">\r\n    <a href=\"/system/cms/files/files/000/000/002/original/Showcase2014Flyer_600.jpg\">\r\n    	<img src=\"/system/cms/files/files/000/000/003/original/Showcase2014Flyer_200.jpg\">\r\n   	</a>\r\n  </div>\r\n  \r\n</div>\